import { Component, Input } from '@angular/core';
import { colourTemplate } from 'src/app/Interfaces/colours';
import { carouselModule } from 'src/app/Interfaces/moduleTemplates';
import { colours } from 'src/app/websiteData/colours';

@Component({
    selector: 'app-carousel',
    templateUrl: './carousel.component.html',
    styleUrls: ['./carousel.component.css']
})
export class CarouselComponent {
	colours: colourTemplate = colours;

	currentPosition: number = 1;

    @Input() module: carouselModule = {
    	type: 'carousel',
        backgroundColour: '',
    	modules: []
	}
}
<div 
    class="container"
    [ngStyle]="{
        'background': module.backgroundColour,
    }"
    #carouselContainer
>

    <app-module-filterer [module]="module.modules[currentPosition]"></app-module-filterer>

    <div class="dots-container">
        <div 
            *ngFor="let image of module.modules; let i = index" 
            class="dot"
            [style.background]="i==currentPosition ? colours.font1 : ''"
            (click)="currentPosition = i"
        >
        </div>
    </div>
</div>

<div 
    class="arrows back"
    [style.top.px]="-carouselContainer.offsetHeight / 2"
    [style.color]="colours.font1"
>
    <i class="fa fa-angle-left"></i>
</div>

<div 
    class="arrows next"
    [style.top.px]="-carouselContainer.offsetHeight / 2"
    [style.color]="colours.font1"
>
    <i class="fa fa-angle-right"></i>
</div>
