import { Injectable } from '@angular/core';

@Injectable({
    providedIn: 'root'
})
export class WindowWidthService {
    windowWidth: number = window.innerWidth

    constructor() { 
        window.addEventListener('resize', () => {
			this.windowWidth = window.innerWidth
    	})
    }
}
