import { Component, Input } from '@angular/core';
import { buttonTemplate, textTemplate, contactPage, imageTemplate, videoTemplate } from '../../Interfaces/contentTemplates';
import { Router } from '@angular/router';
import { DomSanitizer } from '@angular/platform-browser';

@Component({
    selector: 'app-content',
    templateUrl: './content.component.html',
    styleUrls: ['./content.component.css']
})
export class ContentComponent {
	@Input() content: Array<textTemplate | buttonTemplate | imageTemplate | videoTemplate | contactPage> = [];
	hover: boolean = false;

	constructor(private router: Router, public sanitiser: DomSanitizer) {}

	navigate(route: string){
		if(route.slice(0, 4) == 'http'){
  			window.open(route, '_blank');
		}else{
			this.router.navigate(['/' + route]); 
		}
	}

	sanitise(src: string){
		this.sanitiser.bypassSecurityTrustResourceUrl(src)
	}
}
