import { Component, Input } from '@angular/core';
import { colourTemplate } from 'src/app/Interfaces/colours';
import { fontsTemplate } from 'src/app/Interfaces/fonts';
import { fonts, colours } from 'src/app/Interfaces/global';

@Component({
    selector: 'app-module1',
    templateUrl: './module1.component.html',
    styleUrls: ['./module1.component.css']
})
export class Module1Component {
	@Input() imgURL: string = "";
	@Input() heading: string | null = null;
	@Input() subHeading: string | null = null;
	@Input() content: string | null = null;

	fonts: fontsTemplate = fonts;
	colours: colourTemplate = colours
	imgHeight: string = '500px';

	getOffsetHeight(){
		const textContainer = document.getElementById('text-container')
		if(textContainer != null){
			return textContainer.offsetHeight.toString()+'px'
		}
