import { Component, Input } from '@angular/core';
import { colourTemplate } from 'src/app/Interfaces/colours';
import { fontsTemplate } from 'src/app/Interfaces/fonts';
import { fonts, colours } from 'src/app/Interfaces/global';

@Component({
    selector: 'app-module3',
    templateUrl: './module3.component.html',
    styleUrls: ['./module3.component.css']
})
export class Module3Component {
	@Input() heading: string | null = null;
	@Input() subHeading1: string | null = null;
	@Input() content1: string | null = null;
	@Input() subHeading2: string | null = null;
	@Input() content2: string | null = null;

	fonts: fontsTemplate = fonts;
	colours: colourTemplate = colours
}
