import { Component } from '@angular/core';
import { colours, fonts, logoUrl } from '../Interfaces/global';
import { colourTemplate } from '../Interfaces/colours';
import { fontsTemplate } from '../Interfaces/fonts';

@Component({
    selector: 'app-navbar',
    templateUrl: './navbar.component.html',
    styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
	logoUrl: string = logoUrl
	colours: colourTemplate = colours
	fonts: fontsTemplate = fonts;

	position: number = 0
	currentScrollPosition: number = 0

	ngOnInit(){
		window.addEventListener("scroll", () => {
			this.position = Math.max(-200, Math.min(0, this.position + this.currentScrollPosition - window.scrollY))
			this.currentScrollPosition = window.scrollY
			console.log(this.position)
		})
	}
}
